import React from 'react';
import BaseQuestion from './BaseQuestion';

const TextareaQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const inputClass = isError ? "w-full p-2 border border-red-500 rounded" : "w-full p-2 border rounded";

  return (
    <QuestionElement question={question} isError={isError} language={language}>
      <textarea
        className={`h-24 ${inputClass}`}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value)}
        placeholder={question.placeholder || ''}
      />
    </QuestionElement>
  );
};

export default TextareaQuestion;
