import React, { useEffect, useState } from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : (question.min || 0));
  const [hasInteracted, setHasInteracted] = useState(value !== undefined && value !== (question.min || 0));

  const calculateStepSize = () => {
    if (question.marks) {
      const markEntries = Object.entries(question.marks);
      return 100 / (markEntries.length - 1);
    }
    return question.step || 1;
  };

  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
      // If value is different from the default, consider it as interacted
      if (value !== (question.min || 0)) {
        setHasInteracted(true);
      }
    }
  }, [value, question.min]);

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    setHasInteracted(true);
    onChange(question.id, newValue);
  };

  // Determine if we should show the error state
  const showError = isError && question.required && !hasInteracted;

  // Enhanced CSS for the slider to show red thumb when in error state
  const sliderStyles = `
    /* Base styles for the slider thumb */
    input[type=range]::-webkit-slider-thumb {
      -webkit-appearance: none;
      appearance: none;
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: white;
      border: 2px solid #4b5563;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    input[type=range]::-moz-range-thumb {
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: white;
      border: 2px solid #4b5563;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    input[type=range]::-ms-thumb {
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: white;
      border: 2px solid #4b5563;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    /* Error styles for the slider thumb */
    .slider-error::-webkit-slider-thumb {
      background-color: white !important;
      border: 2px solid #ef4444 !important;
      box-shadow: 0 0 0 2px #ef4444 !important;
    }

    .slider-error::-moz-range-thumb {
      background-color: white !important;
      border: 2px solid #ef4444 !important;
      box-shadow: 0 0 0 2px #ef4444 !important;
    }

    .slider-error::-ms-thumb {
      background-color: white !important;
      border: 2px solid #ef4444 !important;
      box-shadow: 0 0 0 2px #ef4444 !important;
    }

    /* Track styles */
    input[type=range]::-webkit-slider-runnable-track {
      width: 100%;
      height: 8px;
      cursor: pointer;
      background: #e5e7eb;
      border-radius: 4px;
    }

    input[type=range]::-moz-range-track {
      width: 100%;
      height: 8px;
      cursor: pointer;
      background: #e5e7eb;
      border-radius: 4px;
    }

    input[type=range]::-ms-track {
      width: 100%;
      height: 8px;
      cursor: pointer;
      background: transparent;
      border-color: transparent;
      color: transparent;
    }

    .slider-error::-webkit-slider-runnable-track {
      background: #fee2e2 !important;
    }

    .slider-error::-moz-range-track {
      background: #fee2e2 !important;
    }

    .slider-error::-ms-track {
      background: #fee2e2 !important;
    }
  `;

  const renderMarksAsColumns = () => {
    if (!question.marks) return null;

    const markEntries = Object.entries(question.marks);

    return (
      <div className="relative w-full pb-12 mt-2" style={{ position: 'relative', width: '100%' }}>
        <div className="absolute flex justify-between" style={{ width: '100%' }}>
          {markEntries.map(([markValue, label], index) => {
            const positionPercent = (index / (markEntries.length - 1)) * 100;
            let textAlignment = "text-center";
            let transformStyle = 'translateX(-50%)';

            if (index === 0) {
              textAlignment = "text-left";
              transformStyle = 'translateX(0)';
            } else if (index === markEntries.length - 1) {
              textAlignment = "text-right";
              transformStyle = 'translateX(-100%)';
            }

            return (
              <div
                key={`mark-${markValue}`}
                className={`absolute ${textAlignment}`}
                style={{ 
                  left: `${positionPercent}%`, 
                  transform: transformStyle,
                  width: index === 0 ? '60px' : index === markEntries.length - 1 ? '60px' : '80px'
                }}
              >
                <div className="mb-1">
                  <span className="block text-xs font-medium text-gray-700">{markValue}</span>
                </div>

                <div>
                  <span className="block text-xs text-gray-600" style={{ 
                    lineHeight: '1.2',
                    maxWidth: '100%',
                    wordWrap: 'break-word',
                    hyphens: 'auto'
                  }}>{label}</span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const unit = question.unit || '';

  return (
    <BaseQuestion question={question} isError={isError} language={language} value={value}>
      <div className="relative">
        {sliderErrorStyles && (
          <style>{sliderErrorStyles}</style>
        )}

        {!question.marks && (
          <div className="flex justify-between">
            <span className="text-lg font-medium">{currentValue}{unit}</span>
          </div>
        )}

        <input
          type="range"
          className={`w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer ${showError ? 'slider-error' : ''}`}
          min={question.min || 0}
          max={question.max || 100}
          step={calculateStepSize()}
          value={currentValue}
          onChange={handleSliderChange}
        />

        {!question.marks && (
          <div className="flex justify-between mt-2">
            <span className="text-xs text-gray-600">{question.min || 0}{unit}</span>
            <span className="text-xs text-gray-600">{question.max || 100}{unit}</span>
          </div>
        )}

        {question.marks && renderMarksAsColumns()}
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
