import React from 'react';
import BaseQuestion from './BaseQuestion';

const EmailQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  // Add email validation by default for email questions
  const questionWithValidation = {
    ...question,
    validation: {
      ...(question.validation || {}),
      email: true
    }
  };

  const inputClass = isError ? "w-full p-2 border border-red-500 rounded" : "w-full p-2 border rounded";

  // Handle input change with validation
  const handleChange = (e) => {
    const newValue = e.target.value;
    onChange(question.id, newValue);
  };

  return (
    <BaseQuestion 
      question={questionWithValidation} 
      isError={isError} 
      language={language}
      value={value}
    >
      <input
        type="email"
        className={inputClass}
        value={value}
        onChange={handleChange}
        placeholder={question.placeholder || ''}
        // Add HTML5 validation for immediate feedback
        required={questionWithValidation.required}
      />
    </BaseQuestion>
  );
};

export default EmailQuestion;
