/**
 * Get CSS classes for input based on error state
 * @param {boolean} hasError - Whether the input has an error
 * @param {string} additionalClasses - Additional CSS classes to add
 * @returns {string} - CSS classes for the input
 */
export const getInputStyles = (hasError, additionalClasses = '') => {
  return `input-base ${hasError ? 'input-error' : ''} ${additionalClasses}`.trim();
};

/**
 * Get CSS classes for container based on error state
 * @param {boolean} hasError - Whether the container has an error
 * @param {string} additionalClasses - Additional CSS classes to add
 * @returns {string} - CSS classes for the container
 */
export const getContainerStyles = (hasError, additionalClasses = '') => {
  return `container-base ${hasError ? 'container-error' : ''} ${additionalClasses}`.trim();
};

