import React, { useState, useEffect } from 'react';
import QuestionElement from './QuestionElement';
import { t } from '../../localization';

const RankedQuestion = ({ 
  question, 
  isError, 
  value = [], 
  onChange, 
  language, 
  isModified = false
}) => {
  // Initialize ranking if needed
  const [ranking, setRanking] = useState(value.length ? value : [...question.options]);
  const [dragPosition, setDragPosition] = useState(-1);
  const [draggedItem, setDraggedItem] = useState(-1);
  const [isDraggingOver, setIsDraggingOver] = useState(false);

  // Update local ranking when value prop changes
  useEffect(() => {
    if (value.length) {
      setRanking(value);
    } else if (question.options.length && !value.length) {
      setRanking([...question.options]);
    }
  }, [value, question.options]);

  // Handle the actual reordering
  const handleReordering = (sourceIndex, targetPosition) => {
    // Skip if dropping onto self or adjacent position
    if (sourceIndex === targetPosition || 
        (sourceIndex === targetPosition - 1 && targetPosition > 0)) {
      return false;
    }

    // Reorder the items based on the indicator position
    const newRanking = [...ranking];
    const [movedItem] = newRanking.splice(sourceIndex, 1);

    // Adjust target index if needed
    let targetIndex = targetPosition;
    if (sourceIndex < targetPosition) {
      targetIndex--;
    }

    newRanking.splice(targetIndex, 0, movedItem);

    // Update local state and notify parent
    setRanking(newRanking);
    onChange(question.id, newRanking, true);
    return true;
  };

  return (
    <div 
      className={`ranked-question-container ${isDraggingOver ? 'bg-blue-50 rounded transition-colors duration-200' : ''}`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <QuestionElement question={question} isError={isError} language={language}>
        <div 
          className={`p-3 border rounded ${isError ? 'border-red-500' : isDraggingOver ? 'border-blue-400' : 'border-gray-300'} relative`}
          onDragOver={(e) => {
            e.preventDefault();
            e.dataTransfer.dropEffect = 'move';

            // Get container dimensions
            const rect = e.currentTarget.getBoundingClientRect();
            const mouseY = e.clientY;

            // Get the first item's position if available
            const firstItem = e.currentTarget.querySelector('li:first-child div');
            const firstItemTop = firstItem ? firstItem.getBoundingClientRect().top : rect.top + 30;

            // Check if we're at the top of the container (position 0)
            if (mouseY < firstItemTop - 5) {
              setDragPosition(0);
              return;
            }

            // Check if we're at the bottom of the container
            if (mouseY > rect.bottom - 20) {
              setDragPosition(ranking.length);
              return;
            }
          }}
          onDrop={(e) => {
            e.preventDefault();

            const sourceIndex = parseInt(e.dataTransfer.getData('sourceIndex'));
            if (isNaN(sourceIndex)) return;

            if (dragPosition >= 0) {
              handleReordering(sourceIndex, dragPosition);
            }

            // Reset drag states
            setDragPosition(-1);
            setDraggedItem(-1);
          }}
        >
          <ul className="space-y-1">
            {ranking.map((item, index) => (
              <li key={item}>
                {/* Add indicator line above first item */}
                {dragPosition === 0 && index === 0 && (
                  <div 
                    className="h-1 mb-1 bg-blue-600 rounded-full shadow-md animate-pulse cursor-pointer"
                    style={{ zIndex: 10 }}
                    onDragOver={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      e.dataTransfer.dropEffect = 'move';
                      setDragPosition(0);
                    }}
                    onDrop={(e) => {
                      e.preventDefault();
                      e.stopPropagation();

                      const sourceIndex = parseInt(e.dataTransfer.getData('sourceIndex'));
                      if (isNaN(sourceIndex)) return;

                      handleReordering(sourceIndex, 0);
                      setDragPosition(-1);
                      setDraggedItem(-1);
                    }}
                  ></div>
                )}

                <div 
                  className={`flex items-center p-2 bg-white border rounded cursor-move hover:bg-gray-50 ${
                    dragPosition === index ? 'border-blue-400' : 'border-gray-200'
                  } ${draggedItem === index ? 'opacity-50' : ''}`}
                  draggable
                  onDragStart={(e) => {
                    e.dataTransfer.setData('sourceIndex', index.toString());
                    e.dataTransfer.effectAllowed = 'move';
                    setDraggedItem(index);
                  }}
                  onDragEnd={() => {
                    setDragPosition(-1);
                    setDraggedItem(-1);
                    setIsDraggingOver(false);
                  }}
                  onDragOver={(e) => {
                    e.preventDefault();
                    e.stopPropagation();

                    const rect = e.currentTarget.getBoundingClientRect();
                    const mouseY = e.clientY;
                    const midpoint = rect.top + rect.height / 2;

                    // Determine if we should show indicator above or below
                    if (mouseY < midpoint) {
                      setDragPosition(index);
                    } else {
                      setDragPosition(index + 1);
                    }
                  }}
                  onDrop={(e) => {
                    e.preventDefault();
                    e.stopPropagation();

                    const sourceIndex = parseInt(e.dataTransfer.getData('sourceIndex'));
                    if (isNaN(sourceIndex)) return;

                    const rect = e.currentTarget.getBoundingClientRect();
                    const mouseY = e.clientY;
                    const midpoint = rect.top + rect.height / 2;

                    // Determine drop position based on mouse position
                    const targetPosition = mouseY < midpoint ? index : index + 1;

                    // Perform the reordering
                    handleReordering(sourceIndex, targetPosition);

                    // Reset drag states
                    setDragPosition(-1);
                    setDraggedItem(-1);
                  }}
                >
                  <div className="flex-shrink-0 mr-2 text-gray-400">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                    </svg>
                  </div>

                  {isModified && (
                    <div className="flex-shrink-0 w-6 h-6 flex items-center justify-center bg-gray-200 rounded-full mr-2">
                      {index + 1}
                    </div>
                  )}

                  <div className="flex-grow">{item}</div>
                </div>

                {/* Insert indicator line below item */}
                {dragPosition === index + 1 && (
                  <div 
                    className="h-1 mt-1 bg-blue-600 rounded-full shadow-md animate-pulse cursor-pointer"
                    style={{ zIndex: 10 }}
                    onDragOver={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      e.dataTransfer.dropEffect = 'move';
                      setDragPosition(index + 1);
                    }}
                    onDrop={(e) => {
                      e.preventDefault();
                      e.stopPropagation();

                      const sourceIndex = parseInt(e.dataTransfer.getData('sourceIndex'));
                      if (isNaN(sourceIndex)) return;

                      handleReordering(sourceIndex, index + 1);
                      setDragPosition(-1);
                      setDraggedItem(-1);
                    }}
                  ></div>
                )}
              </li>
            ))}
          </ul>

          {/* Add a special case for empty lists */}
          {ranking.length === 0 && (
            <div className="p-4 text-center text-gray-500 italic">
              {t('emptyRankedList', language) || 'Drag items here'}
            </div>
          )}
        </div>
      </QuestionElement>
    </div>
  );
};

export default RankedQuestion;
