import React from 'react';
import QuestionElement from './BaseQuestion';

const NumberQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const inputClass = isError ? "w-full p-2 border border-red-500 rounded" : "w-full p-2 border rounded";

  return (
    <QuestionElement question={question} isError={isError} language={language}>
      <input
        type="number"
        className={inputClass}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value === '' ? '' : Number(e.target.value))}
        min={question.min !== undefined ? question.min : undefined}
        max={question.max !== undefined ? question.max : undefined}
        step={question.step !== undefined ? question.step : 1}
        placeholder={question.placeholder || ''}
      />
    </QuestionElement>
  );
};

export default NumberQuestion;
