import React, { useEffect, useState } from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Default to min value if no value is provided, or use the provided value
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : question.min);

  // Update local state when prop value changes
  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
    }
  }, [value]);

  // Handle slider change
  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    onChange(question.id, newValue);
  };

  // Calculate mark positions correctly
  const renderMarks = () => {
    const markEntries = Object.entries(question.marks);
    const min = question.min;
    const max = question.max;
    const range = max - min;

    return markEntries.map(([markValue, label]) => {
      const numericValue = parseInt(markValue, 10);
      const position = ((numericValue - min) / range) * 100;

      return (
        <div 
          key={markValue} 
          className="absolute transform -translate-x-1/2"
          style={{ 
            left: `${position}%`,
          }}
        >
          {/* Tick mark line */}
          <div className="w-[2px] h-[8px] bg-gray-300 mx-auto mb-1"></div>
          {/* Label */}
          <div className="text-xs text-gray-600 whitespace-nowrap px-1 text-center max-w-[60px]">
            <span className="block truncate">{label}</span>
          </div>
        </div>
      );
    });
  };

  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div className="pt-1 pb-10 relative">
        {/* Current value display */}
        <div className="flex justify-between mb-2">
          <span className="text-lg font-medium">{currentValue}</span>
          {question.unit && <span className="text-sm text-gray-500">{question.unit}</span>}
        </div>

        {/* Slider input */}
        <input
          type="range"
          min={question.min}
          max={question.max}
          step={question.step || 1}
          value={currentValue}
          onChange={handleSliderChange}
          className={`w-full h-2 rounded-lg appearance-none cursor-pointer ${
            isError ? 'accent-red-500' : 'accent-blue-600'
          }`}
          style={{ touchAction: 'manipulation' }}
          aria-valuemin={question.min}
          aria-valuemax={question.max}
          aria-valuenow={currentValue}
        />

        {/* Min/Max labels */}
        <div className="flex justify-between text-xs mt-4 text-gray-600">
          <span>{question.min}{question.unit}</span>
          <span>{question.max}{question.unit}</span>
        </div>

        {/* Marks with improved positioning and styling */}
        <div className="absolute w-full left-0 top-[calc(2rem+4px)]">
          {renderMarks()}
        </div>
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
