import React, { useEffect, useState, useRef } from 'react';
import BaseQuestion, { isError } from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  shouldValidate = false, 
  value, 
  onChange, 
  language
}) => {
  const [userInteracted, setUserInteracted] = useState(false);
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : (question.min || 0));
  const sliderRef = useRef(null);
  const isDefaultValue = currentValue === (question.min || 0);
  const { hasError } = isError(question, value, shouldValidate, language);

  const calculateStepSize = () => {
    if (question.marks) {
      const markEntries = Object.entries(question.marks);
      return 100 / (markEntries.length - 1);
    }
    return question.step || 1;
  };

  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
      if (value !== (question.min || 0)) {
        setUserInteracted(true);
      }
    }
  }, [value, question.min]);

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    setUserInteracted(true);
    onChange(question.id, newValue);
  };

  const showError = hasError && question.required && (isDefaultValue || !userInteracted);


  const errorThumbStyles = `
    input[type=range_slider].error-thumb::-webkit-slider-thumb {
      background-color: #ef4444 !important;
      border-color: #ef4444 !important;
    }

    input[type=range_slider].error-thumb::-moz-range-thumb {
      background-color: #ef4444 !important;
      border-color: #ef4444 !important;
    }
  `;

  const renderMarksAsColumns = () => {
    if (!question.marks) return null;

    const markEntries = Object.entries(question.marks);

    return (
      <div className="w-full mt-2" style={{ position: 'relative', height: '40px' }}>
        {markEntries.map(([markValue, label], index) => {
          // Calculate the position as a percentage of the slider width
          const position = (index / (markEntries.length - 1)) * 100;

          let textAlignment = "text-center";
          let translateX = `-50%`;
          if (index > Math.floor(markEntries.length / 2)) {
            translateX = `-60%`;
          } else if (index < Math.floor(markEntries.length / 2)) {
            translateX = `-40%`;
          }

          if (index === 0) {
            textAlignment = "text-left";
            translateX = '5%';
          } else if (index === markEntries.length - 1) {
            textAlignment = "text-right";
            translateX = '-105%';
          }

          return (
            <div
              key={`mark-${markValue}`}
              className={`absolute ${textAlignment}`}
              style={{ 
                left: `${position}%`,
                transform: `translateX(${translateX})`,
                width: '60px'
              }}
            >
              <div>
                <span className={`block text-xs font-medium ${showError ? 'text-red-600' : 'text-gray-700'}`}>
                  {markValue}
                </span>
              </div>

              <div>
                <span className={`block text-xs ${showError ? 'text-red-600' : 'text-gray-600'}`} style={{ 
                  lineHeight: '1.2',
                  maxWidth: '100%',
                  wordWrap: 'break-word',
                  hyphens: 'auto'
                }}>{label}</span>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const unit = question.unit || '';

  return (
    <BaseQuestion 
      question={question} 
      shouldValidate={shouldValidate} 
      language={language}
      value={currentValue}
    >
      <div className={`slider-container ${showError ? 'error' : ''}`}>
        <input
          ref={sliderRef}
          type="range"
          className="slider-input"
          value={currentValue}
          onChange={handleSliderChange}
          min={question.min || 0}
          max={question.max || 100}
          step={calculateStepSize()}
          disabled={showError}
          aria-invalid={showError}
          data-testid={`slider-${question.id}`}
          style={{
            accentColor: showError ? '#ef4444' : '',
          }}
        />
        <div className="slider-labels">
          <span>{question.min || 0}{unit}</span>
          <span>{question.max || 100}{unit}</span>
        </div>
        {showError && (
          <div className="question-error">
            {question.error}
          </div>
        )}
      </div>
      {question.marks && renderMarksAsColumns()}
    </BaseQuestion>
  );
};

export default SliderQuestion;
