import React, { useState } from 'react';
import { t } from '../../localization';

const BaseQuestion = ({ 
  question, 
  isError, 
  children, 
  language,

}) => {
  const { id, label, required, info, hint } = question;
  const [showTooltip, setShowTooltip] = useState(false);

  const labelClass = isError ? "block text-sm font-medium mb-1 text-red-600" : "block text-sm font-medium mb-1";

  // Required field asterisk
  const requiredMark = required ? <span className="text-red-600 ml-1">*</span> : null;

  // Info icon for questions with additional information
  const infoIcon = info ? (
    <span className="ml-1 inline-block relative">
      <span 
        className="w-5 h-5 inline-flex items-center justify-center bg-gray-200 text-gray-700 rounded-full text-xs font-bold cursor-pointer"
        onClick={() => setShowTooltip(!showTooltip)}
        onMouseEnter={() => setShowTooltip(true)}
        onMouseLeave={() => setShowTooltip(false)}
      >
        {t('infoIconLabel', language)}
      </span>
      <span 
        className={`absolute left-0 sm:left-auto sm:right-0 bottom-full mb-2 w-full sm:w-64 p-2 bg-gray-800 text-white text-xs rounded z-10 transition-opacity duration-200 ${
          showTooltip ? 'opacity-100' : 'opacity-0 pointer-events-none'
        }`}
      >
        {info}
      </span>
    </span>
  ) : null;

  // Hint element to be rendered below each question
  const hintElement = hint ? (
    <p className="text-xs text-gray-500 mt-1">{hint}</p>
  ) : null;

  return (
    <div className="mb-4" key={id}>
      {label && (
        <label className={labelClass}>
          {label}
          {requiredMark}
          {infoIcon}
        </label>
      )}
      {children}
      {hintElement}
    </div>
  );
};

export default BaseQuestion;
