import React, { useEffect, useState } from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Initialize state with the provided value or default to min value
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : (question.min || 0));

  // Calculate step size based on question type
  const calculateStepSize = () => {
    if (question.marks) {
      const markEntries = Object.entries(question.marks);
      return 100 / (markEntries.length - 1);
    }
    return question.step || 1;
  };

  // Update local state when prop value changes
  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
    }
  }, [value]);

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    onChange(question.id, newValue);
  };

  const renderMarksAsColumns = () => {
    if (!question.marks) return null;

    const markEntries = Object.entries(question.marks);

    return (
      <div className="relative w-full pb-5 mb-4" style={{ position: 'relative', width: '100%' }}>
        {/* Marks container with better spacing */}
        <div className="mt-4">
          {markEntries.map(([markValue, label], index) => {
            const positionPercent = (index / (markEntries.length - 1)) * 100;
            let textAlignment = "text-center";
            let transformStyle = 'translateX(-50%)';

            if (index === 0) {
              textAlignment = "text-left";
              transformStyle = 'translateX(0)';
            } else if (index === markEntries.length - 1) {
              textAlignment = "text-right";
              transformStyle = 'translateX(-100%)';
            }

            return (
              <div
                key={`mark-${markValue}`}
                className={`absolute ${textAlignment}`}
                style={{ 
                  left: `${positionPercent}%`, 
                  transform: transformStyle,
                  width: index === 0 ? '60px' : index === markEntries.length - 1 ? '60px' : '80px'
                }}
              >
                {/* Value row with better spacing */}
                <div className="mb-1">
                  <span className="block text-xs font-medium text-gray-700">{markValue}</span>
                </div>

                {/* Label row with improved wrapping */}
                <div>
                  <span className="block text-xs text-gray-600" style={{ 
                    lineHeight: '1.2',
                    maxWidth: '100%',
                    wordWrap: 'break-word',
                    hyphens: 'auto'
                  }}>{label}</span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Get unit from question object or default to empty string
  const unit = question.unit || '';

  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div className="relative">
        {/* Current value display - only show for range type */}
        {!question.marks && (
          <div className="flex justify-between">
            <span className="text-lg font-medium">{currentValue}{unit}</span>
          </div>
        )}

        {/* Slider input */}
        <div className="relative">
          <input
            type="range"
            min={question.marks ? 0 : question.min}
            max={question.marks ? 100 : question.max}
            step={calculateStepSize()}
            value={currentValue}
            onChange={handleSliderChange}
            className={`w-full h-6 ${isError ? 'accent-red-500' : 'accent-blue-600'}`}
            style={{ touchAction: 'manipulation' }}
            aria-valuemin={question.marks ? 0 : question.min}
            aria-valuemax={question.marks ? 100 : question.max}
            aria-valuenow={currentValue}
          />

          {/* Render marks if present */}
          {renderMarksAsColumns()}

          {/* Min/Max labels for range type */}
          {!question.marks && (
            <div className="flex justify-between text-xs">
              <span>{question.min}{unit}</span>
              <span>{question.max}{unit}</span>
            </div>
          )}
        </div>
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
