import React from 'react';
import QuestionElement from './QuestionElement';

const EmailQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const inputClass = isError 
    ? "w-full appearance-none bg-white border border-red-500 px-4 py-3 rounded-lg shadow leading-tight focus:outline-none focus:shadow-outline" 
    : "w-full appearance-none bg-white border border-gray-300 hover:border-gray-400 px-4 py-3 rounded-lg shadow leading-tight focus:outline-none focus:shadow-outline";

  return (
    <QuestionElement question={question} isError={isError} language={language}>
      <input
        type="email"
        className={inputClass}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value)}
        placeholder={question.placeholder || ''}
      />
    </QuestionElement>
  );
};

export default EmailQuestion;
