import React, { useState } from 'react';
import BaseQuestion from './BaseQuestion';
import { validators } from './BaseQuestion';
import { t } from '../../localization';

const EmailQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const [customValidationMessage, setCustomValidationMessage] = useState('');

  // Add email validation by default for email questions
  const questionWithValidation = {
    ...question,
    validation: {
      ...(question.validation || {}),
      email: true
    }
  };

  const handleChange = (e) => {
    const newValue = e.target.value;

    // Only perform detailed validation if there's a value
    if (newValue) {
      // Check specific email validation criteria to provide better error messages
      const basicEmailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

      if (!basicEmailRegex.test(newValue)) {
        setCustomValidationMessage(t('validationEmailFormat', language));
      } else {
        const localPart = newValue.split('@')[0];
        const domainPart = newValue.split('@')[1];

        if (localPart.length > 64) {
          setCustomValidationMessage(t('validationEmailLocalLength', language));
        } else if (/^[.]|[.]$/.test(localPart)) {
          setCustomValidationMessage(t('validationEmailLocalDot', language));
        } else if (/[.]{2,}/.test(localPart)) {
          setCustomValidationMessage(t('validationEmailConsecutiveDots', language));
        } else if (domainPart.length > 255) {
          setCustomValidationMessage(t('validationEmailDomainLength', language));
        } else if (!/^[a-zA-Z0-9.-]+$/.test(domainPart)) {
          setCustomValidationMessage(t('validationEmailDomainChars', language));
        } else if (/^[-.]|[-.]$/.test(domainPart)) {
          setCustomValidationMessage(t('validationEmailDomainHyphen', language));
        } else {
          const tld = domainPart.split('.').pop();
          if (!tld || tld.length < 2) {
            setCustomValidationMessage(t('validationEmailTld', language));
          } else {
            setCustomValidationMessage('');
          }
        }
      }
    } else {
      setCustomValidationMessage('');
    }

    onChange(question.id, newValue);
  };

  const inputClass = isError ? "w-full p-2 border border-red-500 rounded" : "w-full p-2 border rounded";

  return (
    <BaseQuestion 
      question={questionWithValidation} 
      isError={isError} 
      language={language}
      value={value}
      validationMessage={customValidationMessage}
    >
      <input
        type="email"
        className={inputClass}
        value={value}
        onChange={handleChange}
        placeholder={question.placeholder || ''}
      />
    </BaseQuestion>
  );
};

export default EmailQuestion;
