import React from 'react';

const CheckboxQuestion = ({ 
  question, 
  isError, 
  value = false, 
  onChange, 
  language 
}) => {
  return (
    <div className="mb-4" key={question.id}>
      <div className={`flex items-center p-4 rounded-lg shadow border ${isError ? 'border-red-500 bg-red-50' : 'border-gray-300 hover:border-gray-400'}`}>
        <input
          type="checkbox"
          id={question.id}
          checked={value}
          onChange={(e) => onChange(question.id, e.target.checked)}
          className="w-4 h-4 mr-3 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
        />
        <label htmlFor={question.id} className={`select-none ${isError ? "text-red-600" : "text-gray-700"}`}>
          {question.label}
          {question.required && <span className="text-red-600 ml-1">*</span>}
          {question.info && (
            <span className="ml-1 inline-block relative group">
              <span className="w-5 h-5 inline-flex items-center justify-center bg-gray-200 text-gray-700 rounded-full text-xs font-bold cursor-help">
                i
              </span>
              <span className="absolute left-0 bottom-full mb-2 w-64 p-2 bg-gray-800 text-white text-xs rounded shadow-lg opacity-0 group-hover:opacity-100 transition-opacity z-10">
                {question.info}
              </span>
            </span>
          )}
        </label>
      </div>
    </div>
  );
};

export default CheckboxQuestion;
