import React, { useEffect, useState, useRef } from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Track if the user has explicitly interacted with the slider
  const [userInteracted, setUserInteracted] = useState(false);

  // Initialize with the provided value or default to min
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : (question.min || 0));

  // Reference to the slider element
  const sliderRef = useRef(null);

  // Calculate if the value is different from the default (min)
  const isDefaultValue = currentValue === (question.min || 0);

  const calculateStepSize = () => {
    if (question.marks) {
      const markEntries = Object.entries(question.marks);
      return 100 / (markEntries.length - 1);
    }
    return question.step || 1;
  };

  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
      // If value is not the default, consider it as interacted
      if (value !== (question.min || 0)) {
        setUserInteracted(true);
      }
    }
  }, [value, question.min]);

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    setUserInteracted(true);
    onChange(question.id, newValue);
  };

  // Show error if:
  // 1. Form validation has been triggered (isError is true)
  // 2. The question is required
  // 3. Either the user hasn't interacted with the slider OR the value is still at default
  const showError = isError && question.required && (isDefaultValue || !userInteracted);

  // Apply error styles directly to the DOM element for better browser compatibility
  useEffect(() => {
    if (sliderRef.current) {
      if (showError) {
        sliderRef.current.style.setProperty('--thumb-border-color', '#ef4444');
        sliderRef.current.style.setProperty('--track-color', '#fee2e2');
      } else {
        sliderRef.current.style.setProperty('--thumb-border-color', '#4b5563');
        sliderRef.current.style.setProperty('--track-color', '#e5e7eb');
      }
    }
  }, [showError]);

  // CSS for the slider with error state using CSS variables for dynamic styling
  const sliderStyles = `
    /* CSS variables for dynamic styling */
    input[type=range] {
      --thumb-border-color: #4b5563;
      --track-color: #e5e7eb;
    }

    /* Reset default styles */
    input[type=range].custom-slider {
      -webkit-appearance: none;
      appearance: none;
      width: 100%;
      height: 8px;
      border-radius: 4px;
      background: var(--track-color);
      outline: none;
      cursor: pointer;
    }

    /* Thumb styles for WebKit browsers (Chrome, Safari) */
    input[type=range].custom-slider::-webkit-slider-thumb {
      -webkit-appearance: none;
      appearance: none;
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: white;
      border: 2px solid var(--thumb-border-color);
      cursor: pointer;
      box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.1);
      transition: border-color 0.2s ease;
    }

    /* Thumb styles for Firefox */
    input[type=range].custom-slider::-moz-range-thumb {
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: white;
      border: 2px solid var(--thumb-border-color);
      cursor: pointer;
      box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.1);
      transition: border-color 0.2s ease;
    }

    /* Track styles for Firefox */
    input[type=range].custom-slider::-moz-range-track {
      background: var(--track-color);
      height: 8px;
      border-radius: 4px;
    }

    /* Focus styles */
    input[type=range].custom-slider:focus {
      outline: none;
    }

    input[type=range].custom-slider:focus::-webkit-slider-thumb {
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.3);
    }

    input[type=range].custom-slider:focus::-moz-range-thumb {
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.3);
    }

    /* Responsive styles for slider marks */
    @media (max-width: 480px) {
      .slider-mark-first {
        text-align: left !important;
        left: 0 !important;
        transform: none !important;
      }

      .slider-mark-middle {
        transform: translateX(-50%) !important;
        max-width: 40px !important;
      }

      .slider-mark-last {
        text-align: right !important;
        left: auto !important;
        right: 0 !important;
        transform: none !important;
      }
    }
  `;

  const renderMarksAsColumns = () => {
    if (!question.marks) return null;

    const markEntries = Object.entries(question.marks);

    return (
      <div className="relative w-full pb-12 mt-2" style={{ position: 'relative', width: '100%' }}>
        <div className="absolute flex justify-between" style={{ width: '100%' }}>
          {markEntries.map(([markValue, label], index) => {
            const positionPercent = (index / (markEntries.length - 1)) * 100;
            let textAlignment = "text-center";
            let transformStyle = 'translateX(-50%)';
            let markClass = "slider-mark-middle";

            if (index === 0) {
              textAlignment = "text-left";
              transformStyle = 'translateX(0)';
              markClass = "slider-mark-first";
            } else if (index === markEntries.length - 1) {
              textAlignment = "text-right";
              transformStyle = 'translateX(-100%)';
              markClass = "slider-mark-last";
            }

            return (
              <div
                key={`mark-${markValue}`}
                className={`absolute ${textAlignment} ${markClass}`}
                style={{ 
                  left: `${positionPercent}%`, 
                  transform: transformStyle,
                  width: 'auto',
                  maxWidth: markEntries.length <= 3 ? '80px' : '60px',
                  fontSize: markEntries.length > 3 ? 'calc(0.65rem + 0.1vw)' : 'inherit'
                }}
              >
                <div className="mb-1">
                  <span className={`block text-xs font-medium ${showError ? 'text-red-600' : 'text-gray-700'}`}>
                    {markValue}
                  </span>
                </div>

                <div>
                  <span 
                    className={`block text-xs ${showError ? 'text-red-600' : 'text-gray-600'}`} 
                    style={{ 
                      lineHeight: '1.2',
                      wordWrap: 'break-word',
                      hyphens: 'auto',
                      display: '-webkit-box',
                      WebkitLineClamp: '2',
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}
                  >
                    {label}
                  </span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const unit = question.unit || '';

  return (
    <BaseQuestion question={question} isError={isError} language={language} value={value}>
      <div className="relative">
        {/* Include the styles in the component */}
        <style>{sliderStyles}</style>

        {!question.marks && (
          <div className="flex justify-between">
            <span className={`text-lg font-medium ${showError ? 'text-red-600' : ''}`}>
              {currentValue}{unit}
            </span>
          </div>
        )}

        <input
          ref={sliderRef}
          type="range"
          className="custom-slider"
          min={question.min || 0}
          max={question.max || 100}
          step={calculateStepSize()}
          value={currentValue}
          onChange={handleSliderChange}
          aria-invalid={showError}
          data-testid={`slider-${question.id}`}
        />

        {!question.marks && (
          <div className="flex justify-between mt-2">
            <span className={`text-xs ${showError ? 'text-red-600' : 'text-gray-600'}`}>
              {question.min || 0}{unit}
            </span>
            <span className={`text-xs ${showError ? 'text-red-600' : 'text-gray-600'}`}>
              {question.max || 100}{unit}
            </span>
          </div>
        )}

        {question.marks && renderMarksAsColumns()}

        {/* Show an explicit error message when in error state */}
        {showError && (
          <p className="text-xs text-red-600 mt-1">
            Please select a value
          </p>
        )}
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
