import React from 'react';
import { t, languages, defaultLanguage } from '../localization';
import { getSurveyData } from '../data/surveyData';
import { getQuestionComponentByType } from './questions';

class InteractiveSurvey extends React.Component {
  constructor(props) {
    super(props);
    const language = props.language || defaultLanguage;
    const surveyData = getSurveyData(language);
    console.log('Constructor - Language:', language);
    console.log('Constructor - Survey Data:', surveyData);

    this.state = {
      currentSection: 0,
      answers: {},
      progress: 0,
      submitted: false,
      validationError: false,
      missingFields: [],
      surveyId: 'survey_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9),
      language: language,
      surveyData: surveyData,
      languageSelected: false
    };
  }

  // Change language
  changeLanguage = (language) => {
    if (languages[language]) {
      const surveyData = getSurveyData(language);
      console.log('Change Language - Language:', language);
      console.log('Change Language - Survey Data:', surveyData);

      this.setState({ 
        language,
        surveyData: surveyData
      });
    }
  }

  // Proceed after language selection
  proceedWithLanguage = () => {
    this.setState({ languageSelected: true });
  }

  // Save answers
  saveAnswer = (questionId, value, isUserAction = false) => {
    this.setState(prevState => {
      const newAnswers = {
        ...prevState.answers,
        [questionId]: value
      };

      // If this is a user action for a ranked question, mark it as modified
      if (isUserAction) {
        newAnswers[`${questionId}_modified`] = true;
      }

      return { answers: newAnswers };
    });
  };

  // Handle multiple answers
  handleMultiSelect = (questionId, option, checked) => {
    const currentSelections = this.state.answers[questionId] || [];

    if (checked) {
      this.saveAnswer(questionId, [...currentSelections, option]);
    } else {
      this.saveAnswer(questionId, currentSelections.filter(item => item !== option));
    }
  };

  // Handle ranking updates
  handleRankingChange = (questionId, newOrder) => {
    // Always mark as a user action when ranking changes
    this.saveAnswer(questionId, newOrder, true);
  };

  // Next section
  nextSection = () => {
    // Validate required fields
    if (!this.validateRequiredFields()) {
      return;
    }

    this.setState(prevState => {
      // If not the last section, go to next section
      if (this.state.currentSection < this.state.surveyData.length - 1) {
        // Scroll to top after state update
        window.scrollTo({ top: 0, behavior: 'smooth' });

        return {
          currentSection: prevState.currentSection + 1,
          validationError: false,
          missingFields: [],
          progress: Math.round(((prevState.currentSection + 1) / (this.state.surveyData.length - 1)) * 100)
        };
      } else {
        // If last section, submit the form
        this.handleSubmit();
        return null;
      }
    });
  };

  // Previous section
  prevSection = () => {
    this.setState(prevState => {
      // Scroll to top after state update
      window.scrollTo({ top: 0, behavior: 'smooth' });

      return {
        currentSection: prevState.currentSection - 1,
        validationError: false,
        missingFields: [],
        progress: Math.round(((prevState.currentSection - 1) / (this.state.surveyData.length - 1)) * 100)
      };
    });
  };

  // Validate required fields
  validateRequiredFields = () => {
    const currentSectionQuestions = this.state.surveyData[this.state.currentSection].questions;
    const missingFields = [];

    const requiredQuestions = currentSectionQuestions.filter(q => q.required && this.shouldShowQuestion(q));

    requiredQuestions.forEach(q => {
      const answer = this.state.answers[q.id];
      if (answer === undefined || answer === '' || (Array.isArray(answer) && answer.length === 0)) {
        missingFields.push(q.id);
      }
    });

    if (missingFields.length > 0) {
      this.setState({
        validationError: true,
        missingFields
      });
      return false;
    }

    this.setState({
      validationError: false,
      missingFields: []
    });
    return true;
  };

  // Save to JSONBin
  saveToJsonBin = (data) => {
    fetch('https://api.jsonbin.io/v3/b', {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'X-Master-Key': '$2a$10$d/tjFfo2wRCJsPoPs307DO2097IW8VsfNdz9xmCcuXQ4IscWPYj1a',
        'X-Bin-Name': this.state.surveyId
      },
      body: JSON.stringify(data)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Saved to JSONBin:', data);
      localStorage.setItem('lastSurveyId', data.metadata.id);
    })
    .catch(error => {
      console.error('Error saving to JSONBin:', error);
      this.saveToLocalStorage(data);
    });
  };

  saveToLocalStorage = (data) => {
    try {
      const existingData = JSON.parse(localStorage.getItem('surveyResponses') || '[]');
      existingData.push({
        id: this.state.surveyId,
        timestamp: new Date().toISOString(),
        data: data
      });
      localStorage.setItem('surveyResponses', JSON.stringify(existingData));
      console.log('Saved to localStorage as fallback');
    } catch (e) {
      console.error('Error saving to localStorage:', e);
    }
  };

  // Submit survey
  handleSubmit = () => {
    console.log("Answers:", this.state.answers);

    // Prepare data for submission
    const submissionData = {
      surveyId: this.state.surveyId,
      answers: this.state.answers,
      submittedAt: new Date().toISOString(),
      userAgent: navigator.userAgent,
      screenSize: `${window.innerWidth}x${window.innerHeight}`
    };

    this.saveToJsonBin(submissionData);

    this.s

    this.setState({ submitted: true });
  };

  // Check if a question should be displayed based on conditional logic
  shouldShowQuestion = (question) => {
    if (!question.condition) return true;

    const { dependsOn, value } = question.condition;
    const answer = this.state.answers[dependsOn];

    // If answer is undefined, the condition is not met
    if (answer === undefined) return false;

    // Handle array values (for multiselect)
    if (Array.isArray(answer)) {
      // If value is also an array, check if any value matches
      if (Array.isArray(value)) {
        return value.some(v => answer.includes(v));
      }
      // If value is a string, check if it's included in the answer array
      return answer.includes(value);
    }

    // Handle boolean values (for checkbox)
    if (typeof answer === 'boolean') {
      return answer === value;
    }

    // Handle function conditions
    if (typeof value === 'function') {
      return value(answer);
    }

    // Default case: direct comparison
    return answer === value;
  };

  // Render function for question types
  renderQuestion = (question) => {
    if (!this.shouldShowQuestion(question)) return null;

    const isRequired = question.required;
    const isError = this.state.validationError && this.state.missingFields.includes(question.id);

    // Get the component for this question type
    const QuestionComponent = getQuestionComponentByType(question.type);

    if (!QuestionComponent) {
      console.warn(`Unknown question type: ${question.type}`);
        return null;
    }

    const value = this.state.answers[question.id];
    const isModified = this.state.answers[`${question.id}_modified`] === true;

    return (
      <QuestionComponent
        key={question.id}
        question={question}
        isError={isError}
        value={value}
        onChange={this.saveAnswer}
        language={this.state.language}
        isModified={isModified}
      />
    );
  };

  renderCurrentSection = () => {
    if (!this.state.surveyData || !Array.isArray(this.state.surveyData) || !this.state.surveyData[this.state.currentSection]) {
      return (
        <div>
          <p>Loading section data...</p>
        </div>
      );
    }

    const section = this.state.surveyData[this.state.currentSection];

    return (
      <div>
        <h2 className="text-xl font-bold mb-2">{section.title}</h2>
        <p className="text-gray-600 mb-6">{section.description}</p>

        {this.state.validationError && (
          <div className="mb-4 p-3 bg-red-50 border border-red-300 rounded text-red-700 text-sm">
            {t('validationError', this.state.language)}
          </div>
        )}

        {section.questions.map(question => this.renderQuestion(question))}

        {this.state.currentSection === 0 && (
          <div className="mt-6 p-3 bg-blue-50 border border-blue-200 rounded text-blue-800 text-sm">
            <p><strong>{t('anonymityNote', this.state.language)}</strong></p>
          </div>
        )}
      </div>
    );
  };

  renderResults = () => {
    return (
      <div className="text-center">
        <h2 className="text-2xl font-bold mb-4">{t('thankYouTitle', this.state.language)}</h2>
        <p className="mb-6">{t('thankYouMessage', this.state.language)}</p>

        <div className="p-6 bg-green-50 border border-green-200 rounded-lg max-w-md mx-auto">
          <div className="w-16 h-16 mx-auto mb-4">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="text-green-600">
              <path fillRule="evenodd" d="M2.25 12c0-5.385 4.365-9.75 9.75-9.75s9.75 4.365 9.75 9.75-4.365 9.75-9.75 9.75S2.25 17.385 2.25 12zm13.36-1.814a.75.75 0 10-1.22-.872l-3.236 4.53L9.53 12.22a.75.75 0 00-1.06 1.06l2.25 2.25a.75.75 0 001.14-.094l3.75-5.25z" clipRule="evenodd" />
            </svg>
          </div>
          <p className="text-green-800">
            {t('thankYouMessage', this.state.language)}
          </p>
          <p className="mt-4 text-sm text-gray-600">
            {t('surveyIdLabel', this.state.language)} <span className="font-mono bg-gray-100 px-2 py-1 rounded">{this.state.surveyId}</span>
          </p>
        </div>
      </div>
    );
  };

  renderLanguageSelectionScreen = () => {
    return (
      <div className="flex flex-col items-center justify-center min-h-[400px]">
        <h1 className="text-3xl font-bold mb-8">{t('welcomeToSurvey', this.state.language)}</h1>

        <div className="mb-8 text-center">
          <p className="text-lg mb-4">{t('pleaseSelectLanguage', this.state.language)}</p>

          <div className="relative inline-block w-64">
            <select
              value={this.state.language}
              onChange={(e) => this.changeLanguage(e.target.value)}
              className="block w-full appearance-none bg-white border border-gray-300 hover:border-gray-400 px-4 py-3 pr-8 rounded-lg shadow leading-tight focus:outline-none focus:shadow-outline text-lg"
            >
              {Object.entries(languages).map(([code, name]) => (
                <option key={code} value={code}>
                  {name}
                </option>
              ))}
            </select>
            <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
              <svg className="fill-current h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/>
              </svg>
            </div>
          </div>
        </div>

        <button
          onClick={this.proceedWithLanguage}
          className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-lg font-medium"
        >
          {t('proceedToSurvey', this.state.language)}
        </button>
      </div>
    );
  }

  // Remove the global drag handlers since we're handling everything within the component
  componentDidMount() {
    // We don't need global handlers anymore
  }

  componentWillUnmount() {
    // No global handlers to clean up
  }

  render() {
    console.log('Render - Survey Data:', this.state.surveyData);

    if (!this.state.surveyData || !Array.isArray(this.state.surveyData)) {
      return (
        <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow">
          <p>Loading survey data...</p>
        </div>
      );
    }

    return (
        <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow">

        {!this.state.languageSelected ? (
          // Show language selection screen if language not yet selected
          this.renderLanguageSelectionScreen()
        ) : !this.state.submitted ? (
          // Show survey if language selected and not yet submitted
          <>
            <h1 className="text-2xl font-bold mb-6">{t('surveyTitle', this.state.language)}</h1>

            {/* Fortschrittsbalken */}
            <div className="mb-8">
              <div className="bg-gray-200 rounded-full h-2.5">
                <div 
                  className="bg-blue-600 h-2.5 rounded-full transition-all duration-300" 
                  style={{ width: `${this.state.progress}%` }}
                ></div>
              </div>
              <div className="flex justify-between mt-1 text-xs text-gray-500">
                <span>{t('startLabel', this.state.language)}</span>
                <span>{this.state.progress}{t('progressLabel', this.state.language)}</span>
              </div>
            </div>

            {this.renderCurrentSection()}

            <div className="mt-8 flex justify-between">
              <button
                onClick={this.prevSection}
                disabled={this.state.currentSection === 0}
                className={`px-4 py-2 rounded transition-colors ${
                  this.state.currentSection === 0 ? 'bg-gray-100 text-gray-400 cursor-not-allowed' : 'bg-gray-200 hover:bg-gray-300'
                }`}
              >
                {t('prevButton', this.state.language)}
              </button>

              <button
                onClick={this.nextSection}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
              >
                {this.state.currentSection === this.state.surveyData.length - 1 ? t('submitButton', this.state.language) : t('nextButton', this.state.language)}
              </button>
            </div>
          </>
        ) : (
          this.renderResults()
        )}
      </div>
    );
  }
}

