import React, { useState, useEffect, useRef } from 'react';
import BaseQuestion, { isError } from './BaseQuestion';

const DragIndicator = ({ position, isDraggingInComponent, setDragPosition, handleReordering }) => (
  <div 
    className="h-1 my-1 bg-blue-600 rounded-full shadow-md animate-pulse cursor-pointer"
    style={{ zIndex: 10 }}
    onDragOver={(e) => {
      if (!isDraggingInComponent) return;
      e.preventDefault();
      e.stopPropagation();
      e.dataTransfer.dropEffect = 'move';
      setDragPosition(position);
    }}
    onDrop={(e) => {
      e.preventDefault();
      e.stopPropagation();

      if (!isDraggingInComponent) return;

      const sourceIndex = parseInt(e.dataTransfer.getData('sourceIndex'));
      if (isNaN(sourceIndex)) return;

      handleReordering(sourceIndex, position);
      setDragPosition(-1);
    }}
  />
);

const RankedQuestion = ({ 
  question, 
  shouldValidate = false, 
  value = [], 
  onChange, 
  language, 
  isModified = false
}) => {
  const { hasError } = isError(question, value, shouldValidate, language);
  const questionId = useRef(`ranked-question-${question.id}`);
  const [ranking, setRanking] = useState(value.length ? value : [...question.options]);
  const [dragPosition, setDragPosition] = useState(-1);
  const [draggedItem, setDraggedItem] = useState(-1);
  const containerRef = useRef(null);
  const [isDraggingInComponent, setIsDraggingInComponent] = useState(false);
  const [hasBeenModified, setHasBeenModified] = useState(false);

  useEffect(() => {
    if (value.length && JSON.stringify(value) !== JSON.stringify(ranking)) {
      setRanking(value);
    } else if (question.options.length && !value.length && JSON.stringify(question.options) !== JSON.stringify(ranking)) {
      setRanking([...question.options]);
    }
  }, [value, question.options, ranking]);

  const handleReordering = (sourceIndex, targetPosition) => {
    if (sourceIndex === targetPosition || 
        (sourceIndex === targetPosition - 1 && targetPosition > 0)) {
      return false;
    }

    const newRanking = [...ranking];
    const [movedItem] = newRanking.splice(sourceIndex, 1);

    let targetIndex = targetPosition;
    if (sourceIndex < targetPosition) {
      targetIndex--;
    }

    newRanking.splice(targetIndex, 0, movedItem);

    setRanking(newRanking);
    onChange(question.id, newRanking, true);
    return true;
  };

  const resetDragStates = () => {
    setDragPosition(-1);
    setDraggedItem(-1);
    setIsDraggingInComponent(false);
  };

  useEffect(() => {
    const handleDragOver = (e) => {
      if (!isDraggingInComponent) return;

      if (containerRef.current && !containerRef.current.contains(e.target)) {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'none';
      }
    };

    document.addEventListener('dragover', handleDragOver);
    return () => {
      document.removeEventListener('dragover', handleDragOver);
    };
  }, [isDraggingInComponent]);

  return (
    <BaseQuestion 
      question={question} 
      shouldValidate={shouldValidate} 
      language={language}
      value={ranking}
    >
      <div 
        ref={containerRef}
        className={`p-4 container-base ${hasError ? 'container-error' : ''}`}
        onDragOver={(e) => {
          if (!isDraggingInComponent) return;
          e.preventDefault();
          e.dataTransfer.dropEffect = 'move';
        }}
        onDragEnter={() => {
          setIsDraggingInComponent(true);
        }}
        onDragLeave={(e) => {
          if (containerRef.current && !containerRef.current.contains(e.relatedTarget)) {
            resetDragStates();
          }
        }}
        onDrop={() => {
          resetDragStates();
        }}
        aria-invalid={hasError}
      >
        <ul className="space-y-1">
          {ranking.map((item, index) => (
            <li key={item}>
              {dragPosition === 0 && index === 0 && (
                <DragIndicator 
                  position={0}
                  index={index}
                  isDraggingInComponent={isDraggingInComponent}
                  setDragPosition={setDragPosition}
                  handleReordering={(sourceIndex, position) => {
                    handleReordering(sourceIndex, position);
                    resetDragStates();
                  }}
                />
              )}

              <div 
                className={`flex items-center p-2 bg-white border rounded cursor-move hover:bg-gray-50 ${
                  dragPosition === index ? 'border-blue-400' : 'border-gray-200'
                } ${draggedItem === index ? 'opacity-50' : ''}`}
                draggable
                onDragStart={(e) => {
                  e.dataTransfer.setData('sourceIndex', index.toString());
                  e.dataTransfer.setData('questionId', questionId.current);
                  e.dataTransfer.effectAllowed = 'move';                 
                  e.dataTransfer.setDragImage(
                    e.currentTarget, 
                    e.currentTarget.offsetWidth / 2, 
                    e.currentTarget.offsetHeight / 2
                  );

                  setIsDraggingInComponent(true);
                  setDraggedItem(index);
                  if (!hasBeenModified) {
                    setHasBeenModified(true);
                    onChange(question.id, ranking, true);
                  }
                }}
                onDragEnd={resetDragStates}
              >
                <span className="flex-grow">{item}</span>
              </div>

              {dragPosition === index + 1 && (
                <DragIndicator 
                  position={index + 1}
                  index={index}
                  isDraggingInComponent={isDraggingInComponent}
                  setDragPosition={setDragPosition}
                  handleReordering={(sourceIndex, position) => {
                    handleReordering(sourceIndex, position);
                    resetDragStates();
                  }}
                />
              )}
            </li>
          ))}
        </ul>
      </div>
    </BaseQuestion>
  );
};

export default RankedQuestion;
