import React from 'react';
import BaseQuestion, { isError } from './BaseQuestion';

const TextQuestion = ({ 
  question, 
  shouldValidate = false, 
  value = '', 
  onChange, 
  language 
}) => {
  const hasError = isError(question, value, shouldValidate);
  const inputClass = hasError 
    ? "w-full p-3 border border-red-500 rounded focus:outline-none focus:ring-2 focus:ring-red-200" 
    : "w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-200";

  return (
    <BaseQuestion 
      question={question} 
      shouldValidate={shouldValidate} 
      language={language}
      value={value}
    >
      <input
        type="text"
        className={inputClass}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value)}
        placeholder={question.placeholder || ''}
      />
    </BaseQuestion>
  );
};

export default TextQuestion;
