import React from 'react';
import BaseQuestion from './BaseQuestion';

const MultiselectQuestion = ({ 
  question, 
  isError, 
  value = [], 
  onChange, 
  language 
}) => {
  const handleMultiSelect = (option, checked) => {
    if (checked) {
      onChange(question.id, [...value, option]);
    } else {
      onChange(question.id, value.filter(item => item !== option));
    }
  };

  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      {question.maxSelect && (
        <span className="text-gray-500 text-xl ml-1">(max. {question.maxSelect})</span>
      )}
      <div className={`space-y-2 p-3 border rounded ${isError ? 'border-red-500' : 'border-gray-300'}`}>
        {question.options.map((option) => {
          const isChecked = value.includes(option);
          const isDisabled = question.maxSelect && 
                          value.length >= question.maxSelect && 
                          !isChecked;

          return (
            <div key={option} className="flex items-center">
              <input
                type="checkbox"
                id={`${question.id}-${option}`}
                checked={isChecked}
                disabled={isDisabled}
                onChange={(e) => handleMultiSelect(option, e.target.checked)}
                className="mr-2"
              />
              <label 
                htmlFor={`${question.id}-${option}`} 
                className={isDisabled ? "text-gray-400" : ""}
              >
                {option}
              </label>
            </div>
          );
        })}
      </div>
    </BaseQuestion>
  );
};

export default MultiselectQuestion;
