import React from 'react';
import QuestionElement from './QuestionElement';
import { t } from '../../localization';

const SelectQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const inputClass = isError 
    ? "w-full appearance-none bg-white border border-red-500 px-4 py-3 pr-8 rounded-lg shadow leading-tight focus:outline-none focus:shadow-outline" 
    : "w-full appearance-none bg-white border border-gray-300 hover:border-gray-400 px-4 py-3 pr-8 rounded-lg shadow leading-tight focus:outline-none focus:shadow-outline";

  return (
    <QuestionElement question={question} isError={isError} language={language}>
      <div className="relative">
        <select
          className={inputClass}
          value={value}
          onChange={(e) => onChange(question.id, e.target.value)}
        >
          <option value="">{t('selectPlaceholder', language)}</option>
          {question.options.map((option) => (
            <option key={option} value={option}>
              {option}
            </option>
          ))}
        </select>
        <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
          <svg className="fill-current h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
            <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/>
          </svg>
        </div>
      </div>
    </QuestionElement>
  );
};

export default SelectQuestion;
