import React, { useEffect, useState } from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Default to min value if no value is provided, or use the provided value
  const [currentValue, setCurrentValue] = useState(value !== undefined ? value : question.min);

  useEffect(() => {
    if (value !== undefined) {
      setCurrentValue(value);
    }
  }, [value]);

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setCurrentValue(newValue);
    onChange(question.id, newValue);
  };

  const renderMarksAsColumns = () => {

  };

  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div className="pt-1 pb-4 relative">
        {/* Current value display */}
        <div className="flex justify-between m-2">
          <span className="text-lg font-medium">{currentValue}</span>
          {question.unit && <span className="text-sm text-gray-500">{question.unit}</span>}
        </div>

        {/* Slider input */}
        <input
          type="range"
          min={question.min}
          max={question.max}
          step={question.step || 1}
          value={currentValue}
          onChange={handleSliderChange}
          className={`w-full h-6 ${isError ? 'accent-red-500' : 'accent-blue-600'}`}
          style={{ touchAction: 'manipulation' }}
          aria-valuemin={question.min}
          aria-valuemax={question.max}
          aria-valuenow={currentValue}
        />

        {renderMarksAsColumns()}
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
