import React from 'react';
import QuestionElement from './QuestionElement';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Default to min value if no value is provided
  const currentValue = value !== undefined ? value : question.min;

  return (
    <QuestionElement question={question} isError={isError} language={language}>
      <div className={`p-4 border rounded-lg shadow ${isError ? 'border-red-500' : 'border-gray-300 hover:border-gray-400'}`}>
        <div className="flex justify-between items-center mb-2">
          <span className="text-lg font-medium">{currentValue}</span>
        </div>
        <input
          type="range"
          min={question.min}
          max={question.max}
          value={currentValue}
          onChange={(e) => onChange(question.id, parseInt(e.target.value))}
          className={`w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer ${isError ? 'accent-red-500' : 'accent-blue-600'}`}
        />
        <div className="flex justify-between text-sm mt-2 text-gray-600">
          {Object.entries(question.marks).map(([value, label]) => (
            <span key={value} className="text-center" style={{width: `${100/(Object.keys(question.marks).length-1)}%`}}>
              {label}
            </span>
          ))}
        </div>
      </div>
    </QuestionElement>
  );
};

export default SliderQuestion;
