import React from 'react';
import BaseQuestion from './BaseQuestion';
import { t } from '../../localization';

const SelectQuestion = ({ 
  question, 
  isError, 
  value = '', 
  onChange, 
  language 
}) => {
  const inputClass = isError 
    ? "w-full p-3 border border-red-500 rounded focus:outline-none focus:ring-2 focus:ring-red-200" 
    : "w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-200";

  return (
    <BaseQuestion question={question} isError={isError} language={language} value={value}>
      <select
        className={`bg-white ${inputClass}`}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value)}
      >
        <option value="">{t('selectPlaceholder', language)}</option>
        {question.options.map((option) => (
          <option key={option} value={option}>
            {option}
          </option>
        ))}
      </select>
    </BaseQuestion>
  );
};

export default SelectQuestion;
