import React from 'react';
import { t } from '../../localization';

const QuestionElement = ({ 
  question, 
  isError, 
  children, 
  language,
  ...otherProps
}) => {
  const { id, label, required, info, hint } = question;

  const labelClass = isError ? "block text-sm font-medium mb-1 text-red-600" : "block text-sm font-medium mb-1";

  // Required field asterisk
  const requiredMark = required ? <span className="text-red-600 ml-1">*</span> : null;

  // Info icon for questions with additional information
  const infoIcon = info ? (
    <span className="ml-1 inline-block relative group">
      <span className="w-4 h-4 inline-flex items-center justify-center bg-gray-200 text-gray-700 rounded-full text-xs font-bold cursor-help">
        {t('infoIconLabel', language)}
      </span>
      <span className="absolute left-0 bottom-full mb-2 w-64 p-2 bg-gray-800 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity z-10">
        {info}
      </span>
    </span>
  ) : null;

  // Hint element to be rendered below each question
  const hintElement = hint ? (
    <p className="text-xs text-gray-500 mt-1">{hint}</p>
  ) : null;

  return (
    <div className="mb-4" key={id}>
      {label && (
        <label className={labelClass}>
          {label}
          {requiredMark}
          {infoIcon}
        </label>
      )}
      {children}
      {hintElement}
    </div>
  );
};

export default QuestionElement;
