import React from 'react';
import BaseQuestion from './BaseQuestion';

const SliderQuestion = ({ 
  question, 
  isError, 
  value, 
  onChange, 
  language 
}) => {
  // Default to min value if no value is provided
  const currentValue = value !== undefined ? value : question.min;

  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div>
        <span className="text-lg font-medium ml-2">{currentValue}</span>
        <input
          type="range"
          min={question.min}
          max={question.max}
          value={currentValue}
          onChange={(e) => onChange(question.id, parseInt(e.target.value))}
          className={`w-full ${isError ? 'accent-red-500' : ''}`}
        />
        <div className="flex justify-between text-xs mt-1">
          {Object.entries(question.marks).map(([value, label]) => (
            <span key={value} className="text-center" style={{width: `${100/(Object.keys(question.marks).length-1)}%`}}>
              {label}
            </span>
          ))}
        </div>
      </div>
    </BaseQuestion>
  );
};

export default SliderQuestion;
