import TextQuestion from './TextQuestion';
import TextareaQuestion from './TextareaQuestion';
import SelectQuestion from './SelectQuestion';
import MultiselectQuestion from './MultiselectQuestion';
import RangeSliderQuestion from './RangeSliderQuestion';
import MarksSliderQuestion from './MarksSliderQuestion';
import NumberQuestion from './NumericQuestion';
import DateQuestion from './DateQuestion';
import RankedQuestion from './RankedQuestion';
import QuestionElement from './BaseQuestion';

export {
  TextQuestion,
  TextareaQuestion,
  SelectQuestion,
  MultiselectQuestion,
  RangeSliderQuestion,
  MarksSliderQuestion,
  NumberQuestion,
  DateQuestion,
  RankedQuestion,
  QuestionElement
};

// Factory function to get the right component by type
export const getQuestionComponentByType = (type) => {
  switch (type) {
    case 'text': return TextQuestion;
    case 'textarea': return TextareaQuestion;
    case 'select': return SelectQuestion;
    case 'multiselect': return MultiselectQuestion;
    case 'range': return RangeSliderQuestion;
    case 'slider': return MarksSliderQuestion;
    case 'number': return NumberQuestion;
    case 'date': return DateQuestion;
    case 'ranked': return RankedQuestion;
    default: return null;
  }
};
