  }

  // Remove the global drag handlers since we're handling everything within the component
  componentDidMount() {
    // We don't need global handlers anymore
  }

  componentWillUnmount() {
    // No global handlers to clean up
  }

  render() {
    console.log('Render - Survey Data:', this.state.surveyData);

    if (!this.state.surveyData || !Array.isArray(this.state.surveyData)) {
      return (
