export const surveyData_en = [{
  title: "Company Profile",
  description: "Basic information about your company",
  questions: [{
    id: "company_name",
    type: "text",
    label: "Company name (optional)"
  }, {
    id: "position",
    type: "text",
    label: "Position of respondent",
    info: "e.g. CEO, Process Manager alternatively department"
  }, {
    id: "industry",
    type: "select",
    label: "Industry",
    required: true,
    options: ["Manufacturing/Production", "Retail/E-Commerce", "Logistics/Transport", "IT Services", "Other Services", "Crafts/Trades", "Healthcare", "Other"]
  }, {
    id: "industry_other",
    type: "text",
    label: "Please specify your industry",
    required: true,
    condition: {
      dependsOn: "industry",
      value: "Other"
    }
  }, {
    id: "company_size",
    type: "select",
    label: "Company size (number of employees)",
    required: true,
    options: ["1-9 employees", "10-49 employees", "50-249 employees", "Over 250 employees"]
  }, {
    id: "revenue",
    type: "select",
    label: "Annual revenue",
    required: true,
    options: ["Under €2 million", "€2 to €10 million", "€10 to €50 million", "Over €50 million"]
  }, {
    id: "location",
    type: "text",
    label: "Location(s) (state/states)"
  }]
}, {
  title: "IT Infrastructure",
  description: "Existing systems and their integration",
  questions: [{
    id: "systems",
    type: "multiselect",
    label: "Which systems do you currently use in your company?",
    required: true,
    options: ["ERP System (e.g., SAP, Microsoft Dynamics, sage)", "CRM System (e.g., Salesforce, HubSpot)", "MES (Manufacturing Execution System)", "BI/Analytics Tools (e.g., Power BI, Tableau)", "Cloud-based Solutions", "IoT Devices/Sensors", "AI/Machine Learning Applications", "Other"]
  }, {
    id: "systems_other",
    type: "text",
    label: "Please specify your systems (other than the ones listed above)",
    required: true,
    condition: {
      dependsOn: "systems",
      value: "Other"
    }
  }, {
    id: "integration_level",
    type: "select",
    label: "How would you rate the current integration level of your IT systems?",
    info: "e.g. Fully integrated (all systems communicate seamlessly with each other), Partially integrated (some systems are connected), Barely integrated (isolated solutions, manual data transfer), No integration",
    required: true,
    options: ["Fully integrated (all systems communicate seamlessly with each other)", "Partially integrated (some systems are connected)", "Barely integrated (isolated solutions, manual data transfer)", "No integration"]
  }, {
    id: "island_solutions_details",
    type: "textarea",
    label: "Please describe your isolated solutions in more detail. Which systems are affected and how is data exchanged?",
    info: "Describe systems or affected business processes",
    condition: {
      dependsOn: "integration_level",
      value: "Barely integrated (isolated solutions, manual data transfer)"
    }
  }, {
    id: "integration_challenges",
    type: "multiselect",
    label: "What challenges do you see in integrating your systems?",
    options: ["Technical complexity", "Cost of integration/interfaces", "Lack of standards/compatibility", "Insufficient expertise", "Legacy systems", "Data security concerns", "Other"],
    condition: {
      dependsOn: "integration_level",
      value: ["Partially integrated (some systems are connected)", "Barely integrated (isolated solutions, manual data transfer)", "No integration"]
    }
  }, {
    id: "data_storage",
    type: "select",
    label: "Where is your company data primarily stored?",
    required: true,
    options: ["Own servers (on-premise)", "Cloud services (e.g., AWS, Azure, Google Cloud)", "Hybrid (combination of both)", "Predominantly paper-based/in local files"]
  }, {
    id: "paper_based_reasons",
    type: "multiselect",
    label: "What are the main reasons for paper-based/local data storage?",
    options: ["Habit/historically grown", "Data privacy concerns", "Digitalization costs", "Lack of expertise for transition", "Specific regulatory requirements", "Other"],
    condition: {
      dependsOn: "data_storage",
      value: "Predominantly paper-based/in local files"
    }
  }]
}, {
  title: "Data Usage and Availability",
  description: "Collection and analysis of company data",
  questions: [{
    id: "data_types",
    type: "multiselect",
    label: "What types of data do you systematically collect in your company?",
    required: true,
    options: ["Customer data", "Sales/revenue data", "Production data", "Machine data/sensor data", "Logistics/delivery data", "Employee data (e.g., working hours, performance metrics)", "Website/e-commerce data", "Other"]
  }, {
    id: "data_freshness",
    type: "select",
    label: "How current is the data you work with?",
    required: true,
    options: ["Real-time/near real-time (seconds/minutes)", "Daily updates", "Weekly updates", "Monthly updates"]
  }, {
    id: "realtime_data_importance",
    type: "slider",
    label: "How important would real-time data availability be for optimizing your business processes?",
    min: 1,
    max: 10,
    marks: {
      1: "Unimportant",
      5: "Neutral",
      10: "Essential"
    },
    condition: {
      dependsOn: "data_freshness",
      value: ["Daily updates", "Weekly updates", "Monthly updates"]
    }
  }, {
    id: "data_analysis",
    type: "select",
    label: "How is data analysis predominantly performed in your company?",
    required: true,
    options: ["Automated with specialized analytics tools", "Semi-automated (e.g., with Excel analyses)", "Manual/sporadic", "No systematic analysis"]
  }, {
    id: "manual_analysis_details",
    type: "textarea",
    label: "Please describe your manual analysis process in more detail. How often do you analyze data and what tools/methods do you use?",
    condition: {
      dependsOn: "data_analysis",
      value: "Manual/sporadic"
    }
  }, {
    id: "kpi_tracking",
    type: "select",
    label: "Are KPIs (Key Performance Indicators) systematically recorded and evaluated in your company?",
    required: true,
    options: ["Yes, comprehensively and in real-time", "Yes, regularly (daily/weekly)", "Yes, but only monthly/quarterly", "No systematic KPI tracking"]
  }, {
    id: "kpi_types",
    type: "multiselect",
    label: "Which KPIs do you track regularly?",
    options: ["Financial KPIs (revenue, profit, etc.)", "Productivity metrics", "Quality metrics", "Customer-related KPIs", "Machine-related KPIs (OEE, downtime, etc.)", "Process-related KPIs (cycle times, etc.)", "Energy consumption/sustainability", "Other"],
    condition: {
      dependsOn: "kpi_tracking",
      value: ["Yes, comprehensively and in real-time", "Yes, regularly (daily/weekly)", "Yes, but only monthly/quarterly"]
    }
  }]
}, {
  title: "Process Automation",
  description: "Level of automation in your company",
  questions: [{
    id: "automated_processes",
    type: "multiselect",
    label: "Which of your business processes are already (partially) automated?",
    required: true,
    options: ["Customer management/CRM", "Order processing", "Production control", "Warehouse management/logistics", "Accounting/finance", "Marketing/sales", "Human resources management", "Quality assurance", "None"]
  }, {
    id: "automation_level",
    type: "slider",
    label: "How would you rate the overall level of automation in your company?",
    required: true,
    min: 1,
    max: 10,
    marks: {
      1: "Barely automated",
      5: "Partially automated",
      10: "Highly automated"
    }
  }, {
    id: "automation_barriers",
    type: "multiselect",
    label: "What are the biggest obstacles to more automation in your company?",
    options: ["Costs", "Process complexity", "Lack of expertise", "Employee resistance", "Inadequate IT infrastructure", "Uncertainty about ROI", "No management priority", "Other"],
    condition: {
      dependsOn: "automation_level",
      value: val => val && val < 7
    }
  }, {
    id: "decision_making",
    type: "select",
    label: "How are decisions made when process deviations occur (e.g., delivery delays, quality issues)?",
    required: true,
    options: ["Automatically through systems", "Semi-automatically (system suggests, human decides)", "Manually according to established rules", "Ad-hoc/situational"]
  }, {
    id: "ai_acceptance",
    type: "slider",
    label: "How high is the acceptance for AI-supported decisions in your company?",
    min: 1,
    max: 10,
    marks: {
      1: "Very low",
      5: "Neutral",
      10: "Very high"
    },
    condition: {
      dependsOn: "decision_making",
      value: ["Automatically through systems", "Semi-automatically (system suggests, human decides)"]
    }
  }]
}, {
  title: "Potentials and Challenges",
  description: "Identification of optimization potentials",
  questions: [{
    id: "process_challenges",
    type: "multiselect",
    label: "What challenges do you currently see in your business processes?",
    required: true,
    options: ["Excessively long processing times", "High error rates", "Lack of transparency", "Inefficient resource utilization", "Delayed response times to problems", "Insufficient customer satisfaction", "High manual efforts", "Other"]
  }, {
    id: "other_challenges",
    type: "text",
    label: "Please describe the other challenges",
    condition: {
      dependsOn: "process_challenges",
      value: val => val && val.includes("Other")
    }
  }, {
    id: "high_manual_efforts",
    type: "textarea",
    label: "Please describe in more detail which areas have particularly high manual efforts",
    condition: {
      dependsOn: "process_challenges",
      value: val => val && val.includes("High manual efforts")
    }
  }, {
    id: "transparency_issues",
    type: "textarea",
    label: "Please describe in more detail which areas lack transparency",
    condition: {
      dependsOn: "process_challenges",
      value: val => val && val.includes("Lack of transparency")
    }
  }, {
    id: "optimization_areas",
    type: "multiselect",
    label: "In which areas do you see the greatest potential for process optimization through real-time data? (Please select the 3 most important)",
    required: true,
    options: ["Production planning and control", "Quality assurance", "Maintenance", "Inventory optimization", "Customer service", "Marketing/sales", "Personnel planning", "Supply chain/logistics optimization", "Energy management", "Other"],
    maxSelect: 3
  }, {
    id: "kpi_improvements",
    type: "multiselect",
    label: "Which specific KPIs would you like to improve through process optimization? (Please select the 3 most important)",
    required: true,
    options: ["Productivity/output per employee", "Cycle times", "Machine utilization/OEE", "Quality rate/scrap rate", "On-time delivery", "Inventory turnover", "Customer acquisition costs (CAC)", "Customer lifetime value (CLV)", "Customer satisfaction/Net Promoter Score", "Energy consumption per product unit", "Other"],
    maxSelect: 3
  }]
}, {
  title: "Innovation Readiness and Resources",
  description: "Assessment of innovation readiness and available resources",
  questions: [{
    id: "digitalization_importance",
    type: "select",
    label: "How would you rate the importance of digitalization in your company strategy?",
    required: true,
    options: ["Very high (central strategic priority)", "High (important, but alongside other priorities)", "Moderate (taken into account)", "Low (not a priority)"]
  }, {
    id: "digitalization_projects",
    type: "select",
    label: "Have you conducted digitalization projects in the last 3 years?",
    required: true,
    options: ["Yes, several major projects", "Yes, one major project", "Yes, but only smaller initiatives", "No"]
  }, {
    id: "project_success",
    type: "select",
    label: "If yes, how would you rate the success of these projects?",
    options: ["Very successful (exceeded goals)", "Successful (achieved goals)", "Partially successful (achieved some goals)", "Not successful (did not achieve goals)", "Cannot evaluate"],
    condition: {
      dependsOn: "digitalization_projects",
      value: ["Yes, several major projects", "Yes, one major project", "Yes, but only smaller initiatives"]
    }
  }, {
    id: "success_factors",
    type: "textarea",
    label: "What were the most important success factors for your digitalization projects?",
    condition: {
      dependsOn: "project_success",
      value: ["Very successful (exceeded goals)", "Successful (achieved goals)"]
    }
  }, {
    id: "failure_factors",
    type: "textarea",
    label: "What were the main reasons for the limited success?",
    condition: {
      dependsOn: "project_success",
      value: ["Partially successful (achieved some goals)", "Not successful (did not achieve goals)"]
    }
  }, {
    id: "management_ai_attitude",
    type: "select",
    label: "How does your management view AI and automated decision-making?",
    required: true,
    options: ["Very receptive and actively driving it forward", "Generally positive but waiting", "Skeptical, needs convincing", "Resistant"]
  }, {
    id: "employee_tech_openness",
    type: "select",
    label: "How would you assess your employees' openness to new technologies and process changes?",
    options: ["Very open and actively participating", "Generally open but with reservations", "Rather reserved/skeptical", "Resistant/resistance expected"]
  }, {
    id: "digital_budget",
    type: "select",
    label: "What budget could you envision for a project to implement dynamic process intelligence?",
    required: true,
    options: ["Under €10,000", "€10,000 - €50,000", "€50,000 - €100,000", "Over €100,000", "Cannot specify"]
  }, {
    id: "internal_resources",
    type: "select",
    label: "What internal resources are available for digitalization projects?",
    options: ["Dedicated IT department with relevant expertise", "Individual IT staff", "Subject matter experts with IT affinity", "No specialized resources"]
  }]
}, {
  title: "Industry-Specific Questions",
  description: "Specific questions depending on company type",
  questions: [
  // Manufacturing companies
  {
    id: "manufacturing_type",
    type: "select",
    label: "What type of manufacturing do you predominantly operate?",
    required: true,
    options: ["Mass production", "Series production", "Small-batch production", "One-off production", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  }, {
    id: "production_monitoring",
    type: "select",
    label: "How are your production facilities currently monitored?",
    required: true,
    options: ["Complete digital monitoring in real-time", "Partial digital monitoring", "Manual monitoring with regular checks", "Only in case of problems/reactive", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  }, {
    id: "maintenance_approach",
    type: "select",
    label: "How is the maintenance of your production facilities predominantly carried out?",
    required: true,
    options: ["Predictive maintenance", "Condition-based maintenance", "Scheduled interval maintenance", "Reactive maintenance (upon failure)", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  },
  // Maintenance questions (dynamically added)
  {
    id: "maintenance_costs",
    type: "select",
    label: "How high do you estimate the share of maintenance costs in your total operating costs?",
    required: true,
    options: ["Under 5%", "5-10%", "11-15%", "16-20%", "Over 20%", "Cannot specify"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  }, {
    id: "maintenance_concept",
    type: "multiselect",
    label: "Which maintenance concepts do you currently use?",
    required: true,
    options: ["Reactive maintenance (repair after failure)", "Preventive maintenance (according to schedule)", "Predictive maintenance (condition-dependent)", "Prescriptive maintenance (AI-supported)", "Total Productive Maintenance (TPM)", "Reliability Centered Maintenance (RCM)", "Other"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  }, {
    id: "maintenance_challenges",
    type: "multiselect",
    label: "What are the biggest challenges in your maintenance?",
    required: true,
    options: ["Unplanned downtime", "High maintenance costs", "Lack of skilled personnel", "Spare parts management", "Lack of data/transparency", "Inadequate documentation", "Equipment complexity", "Other"],
    condition: {
      dependsOn: "industry",
      value: ["Manufacturing/Production", "Crafts/Trades"]
    }
  },
  // Service companies
  {
    id: "customer_channels",
    type: "multiselect",
    label: "Which customer interaction channels do you use?",
    required: true,
    options: ["Personal consultation", "Telephone", "Email", "Website/online shop", "Chat/chatbot", "Social media", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: ["Retail/E-Commerce", "IT Services", "Other Services", "Healthcare"]
    }
  }, {
    id: "feedback_analysis",
    type: "select",
    label: "How is customer feedback collected and analyzed?",
    required: true,
    options: ["Systematically and evaluated in real-time", "Regularly collected and evaluated", "Sporadically/occasionally collected", "No systematic collection", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: ["Retail/E-Commerce", "IT Services", "Other Services", "Healthcare"]
    }
  },
  // Logistics companies
  {
    id: "logistics_tracking",
    type: "select",
    label: "How are shipments/vehicles tracked?",
    required: true,
    options: ["Real-time tracking with permanent status updates", "Step-by-step tracking at defined points", "Manual status updates by staff", "No systematic tracking", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: "Logistics/Transport"
    }
  }, {
    id: "route_planning",
    type: "select",
    label: "How is route planning done in your company?",
    required: true,
    options: ["Dynamic and automated (considering real-time traffic data)", "Static with manual adjustments when needed", "Completely manual and experience-based", "Not applicable"],
    condition: {
      dependsOn: "industry",
      value: "Logistics/Transport"
    }
  }]
}, {
  title: "Implementation Strategies",
  description: "Preferences for implementation",
  questions: [{
    id: "implementation_approach",
    type: "select",
    label: "Which implementation approach would be most suitable for you?",
    required: true,
    options: ["Comprehensive solution for the entire company", "Area-specific solutions (e.g., production only)", "Pilot project with later expansion", "Step-by-step introduction starting with critical processes"]
  }, {
    id: "software_preference",
    type: "select",
    label: "Do you prefer standard software solutions or customized developments?",
    required: true,
    options: ["Standard software (SaaS/Cloud)", "Customized standard software", "Custom development", "Hybrid approach (combination)"]
  }, {
    id: "quick_implementation",
    type: "select",
    label: "How important is a quick implementation to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "low_initial_cost",
    type: "select",
    label: "How important are low initial costs to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "integration",
    type: "select",
    label: "How important is good integration with existing systems to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "scalability",
    type: "select",
    label: "How important is scalability/expandability to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "ease_of_use",
    type: "select",
    label: "How important is ease of use/low complexity to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "data_security",
    type: "select",
    label: "How important are data security/data protection to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }, {
    id: "data_location",
    type: "select",
    label: "How important is the location of data storage (Germany/EU) to you?",
    options: ["Very important", "Important", "Less important", "Unimportant"]
  }]
}, {
  title: "Expectations and ROI",
  description: "Expectations for implementing dynamic process intelligence",
  questions: [{
    id: "primary_goals",
    type: "multiselect",
    label: "What primary goals do you pursue with the implementation of dynamic process intelligence? (Please select the 3 most important)",
    required: true,
    options: ["Cost reduction", "Efficiency improvement", "Quality improvement", "Increased customer satisfaction", "Faster responsiveness", "Better decision-making basis", "Employee workload reduction", "Competitive advantages", "Enable new business models", "Other"],
    maxSelect: 3
  }, {
    id: "expected_roi",
    type: "select",
    label: "What ROI (Return on Investment) do you expect from such a project?",
    required: true,
    options: ["ROI within 6 months", "ROI within 1 year", "ROI within 2 years", "ROI within 3+ years", "No specific ROI expectations"]
  }, {
    id: "productivity_improvement",
    type: "range",
    label: "What productivity increase (in %) do you expect from real-time process intelligence?",
    required: true,
    min: 0,
    max: 100,
    step: 5
  }, {
    id: "error_reduction",
    type: "range",
    label: "What error reduction (in %) do you expect?",
    min: 0,
    max: 100,
    step: 5
  }, {
    id: "cycle_time_reduction",
    type: "range",
    label: "What cycle time reduction (in %) do you expect?",
    min: 0,
    max: 100,
    step: 5
  }, {
    id: "cost_reduction",
    type: "range",
    label: "What cost reduction (in %) do you expect?",
    min: 0,
    max: 100,
    step: 5
  }, {
    id: "revenue_increase",
    type: "range",
    label: "What revenue increase (in %) do you expect?",
    min: 0,
    max: 100,
    step: 5
  }]
}, {
  title: "Barriers and Support Needs",
  description: "Identification of obstacles and support opportunities",
  questions: [{
    id: "main_barriers",
    type: "multiselect",
    label: "What main barriers do you see in implementing dynamic process intelligence?",
    required: true,
    options: ["High investment costs", "Lack of internal expertise", "Integration with existing systems", "Data protection/IT security concerns", "Acceptance issues among employees", "Unclear ROI/economic efficiency", "Insufficient process standardization", "Lack of time", "Other"]
  }, {
    id: "support_needs",
    type: "multiselect",
    label: "In which areas do you need support for implementation?",
    required: true,
    options: ["Strategy development/roadmap", "Process analysis and optimization", "Selection of suitable technologies", "Technical implementation", "Data protection/IT security", "Change management/employee qualification", "Funding advice", "Economic efficiency calculation/ROI analysis", "Other"]
  }, {
    id: "support_format",
    type: "multiselect",
    label: "What form of support would you prefer?",
