    id: "funding_knowledge",
    type: "select",
    label: "Are you aware of funding opportunities for digitalization projects?",
    options: ["Yes, we already use them", "Yes, but we don't use them", "No, but we are interested", "No, not interested"]
  }, {
    id: "used_funding",
    type: "multiselect",
    label: "Which funding programs do you use or have you already used?",
    options: ["Digitalbonus Bayern", "go-digital (BMWi)", "Digital Jetzt", "ZIM (Central Innovation Program for SMEs)", "KfW funding", "Regional funding programs", "EU funding programs", "Other"],
