import React from 'react';
import BaseQuestion from './BaseQuestion';

const CheckboxQuestion = ({ 
  question, 
  isError, 
  value = false, 
  onChange, 
  language 
}) => {
  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div className={`flex items-center p-2 rounded ${isError ? 'bg-red-50' : ''}`}>
        <input
          type="checkbox"
          id={question.id}
          checked={value}
          onChange={(e) => onChange(question.id, e.target.checked)}
          className="mr-2"
        />
        <label htmlFor={question.id} className={isError ? "text-red-600" : ""}>
          {question.label}
          {question.required && <span className="text-red-600 ml-1">*</span>}
          {question.info && (
            <span className="ml-1 inline-block relative group">
              <span className="w-5 h-5 inline-flex items-center justify-center bg-gray-200 text-gray-700 rounded-full text-xs font-bold cursor-help">
                i
              </span>
              <span className="absolute left-0 bottom-full mb-2 w-64 p-2 bg-gray-800 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity z-10">
                {question.info}
              </span>
            </span>
          )}
        </label>
      </div>
    </BaseQuestion>
  );
};

export default CheckboxQuestion;
