import React from 'react';
import BaseQuestion, { isError } from './BaseQuestion';
import { getInputStyles } from '../../utils/inputStyles';

const DateQuestion = ({ 
  question, 
  shouldValidate = false, 
  value = '', 
  onChange, 
  language 
}) => {
  const questionWithValidation = {
    ...question,
    validation: {
      ...(question.validation || {}),
      minDate: question.minDate !== undefined ? question.minDate : undefined,
      maxDate: question.maxDate !== undefined ? question.maxDate : undefined
    }
  };

  // Remove undefined validation rules
  if (questionWithValidation.validation.minDate === undefined) {
    delete questionWithValidation.validation.minDate;
  }
  if (questionWithValidation.validation.maxDate === undefined) {
    delete questionWithValidation.validation.maxDate;
  }

  const { hasError } = isError(questionWithValidation, value, shouldValidate, language);
  const inputClass = getInputStyles(hasError);

  return (
    <BaseQuestion 
      question={questionWithValidation} 
      shouldValidate={shouldValidate} 
      language={language} 
      value={value}
    >
      <input
        type="date"
        className={inputClass}
        value={value}
        onChange={(e) => onChange(question.id, e.target.value)}
        min={question.minDate || undefined}
        max={question.maxDate || undefined}
        aria-invalid={hasError}
      />
    </BaseQuestion>
  );
};

export default DateQuestion;
