import React, { useState, useRef, useEffect } from 'react';
import { t } from '../../localization';

const BaseQuestion = ({ 
  question, 
  isError, 
  children, 
  language,
}) => {
  const { id, label, required, info, hint } = question;
  const [showTooltip, setShowTooltip] = useState(false);
  const [tooltipWidth, setTooltipWidth] = useState(0);
  const tooltipRef = useRef(null);
  const textMeasureRef = useRef(null);

  const labelClass = isError ? "block text-sm font-medium mb-1 text-red-600" : "block text-sm font-medium mb-1";

  useEffect(() => {
    if (showTooltip && info && textMeasureRef.current) {
      // Create temporary span to measure text
      const span = document.createElement('span');
      span.style.visibility = 'hidden';
      span.style.position = 'absolute';
      span.style.whiteSpace = 'nowrap';
      span.style.fontSize = '0.875rem'; // text-sm
      span.style.padding = '0.5rem'; // p-2
      span.textContent = info;
      document.body.appendChild(span);

      // Get the width and add some padding
      const textWidth = span.offsetWidth + 16; // Add 16px padding
      document.body.removeChild(span);

      // Set width with max constraint handled by CSS
      setTooltipWidth(textWidth);
    }
  }, [showTooltip, info]);

  // Handle clicks outside the tooltip
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (tooltipRef.current && !tooltipRef.current.contains(event.target)) {
        setShowTooltip(false);
      }
    };

    if (showTooltip) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showTooltip]);

  // Required field asterisk
  const requiredMark = required ? <span className="text-red-600 ml-1">*</span> : null;

  const infoIcon = info ? (
    <span className="ml-1 inline-block relative" ref={tooltipRef}>
      <span 
        className="w-5 h-5 inline-flex items-center justify-center bg-gray-200 text-gray-700 rounded-full text-xs font-bold cursor-pointer hover:bg-gray-300"
        onClick={() => setShowTooltip(!showTooltip)}
        aria-label={showTooltip ? t('hideInfo', language) : t('showInfo', language)}
      >
        {t('infoIconLabel', language)}
      </span>
      {showTooltip && (
        <span 
          ref={textMeasureRef}
          style={{ width: `${tooltipWidth}px` }}
          className="absolute left-0 top-full mt-1 p-2 bg-gray-800 text-white text-sm rounded shadow-lg z-10 max-w-[calc(60vw-2rem)] whitespace-normal"
        >
          {info}
        </span>
      )}
    </span>
  ) : null;

  // Hint element to be rendered below each question
  const hintElement = hint ? (
    <p className="text-xs text-gray-500 mt-1">{hint}</p>
  ) : null;

  return (
    <div className="mb-4" key={id}>
      {label && (
        <label className={labelClass}>
          {label}
          {requiredMark}
          {infoIcon}
        </label>
      )}
      {children}
      {hintElement}
    </div>
  );
};

export default BaseQuestion;
