import React from 'react';
import BaseQuestion from './BaseQuestion';

const RangeQuestion = ({ 
  question, 
  isError, 
  value = 0, 
  onChange, 
  language 
}) => {
  return (
    <BaseQuestion question={question} isError={isError} language={language}>
      <div>
        <span className="text-lg font-medium ml-2">{value}%</span>
        <input
          type="range"
          min={question.min}
          max={question.max}
          step={question.step}
          value={value}
          onChange={(e) => onChange(question.id, parseInt(e.target.value))}
          className={`w-full ${isError ? 'accent-red-500' : ''}`}
        />
        <div className="flex justify-between text-xs mt-1">
          <span>{question.min}%</span>
          <span>{question.max}%</span>
        </div>
      </div>
    </BaseQuestion>
  );
};

export default RangeQuestion;
